mapper.select:
select ch_url, ch_name, sub, category, img, adcount from channel where category = #{category}
and sub between 0 and 100000 and sub between 100000 and 1000000 and sub between 1000000 and 10000000 and sub between 10000000 and 1000000000
limit #{pageStart}, #{perPageNum}

select upday,name,likecnt,ad_url
from advideo
where ch_url=#{url}

select count(*)
from customer
where id = #{id}

select count(*)
from customer
where regnum = #{regnum}

SELECT ch_url, ch_NAME, adavgview, img FROM channel WHERE ch_name IN ( SELECT NAME FROM advideo WHERE
hashtag like #{arr} or descript like #{arr}
GROUP BY NAME ) ORDER BY adavgview DESC limit 0,5;
